import osimport pandas as pdimport glob# Set work dir (where mutation tsv files are located)WORK_DIR = "/Users/davidlord/Documents/External_data/script_running/"os.chdir(WORK_DIR)    # Read all mutation files in dirfile_list = glob.glob(WORK_DIR + "*.mutation.tsv")# Create an empty dict (to store mutation file data frames in)file_dict = {}for f in file_list:    # Extract filename    filename = os.path.basename(f)    # Read as pandas dataframe    data = pd.read_csv(f)    df = pd.DataFrame(data)    # Store in dict    file_dict[filename] = df# delete intermediate variablesdead_vars = [df, f, filename, data]for i in dead_vars:    del(i)