# #!/bin/python# Read input (apply later when running on hpc)# input = raw_input('vcf file: ')# Set wdimport osimport globimport pandas as pd# Read all vcf files in dirfile_list = glob.glob("*.vcf")for f in file_list:    # Get file name    filename = os.path.basename(f)    # Open file    vcf = open(filename)    # Create intermediate file    intermediate_file = str.removesuffix(filename, ".vcf") + ".intermediate.vcf"    ivcf = open(intermediate_file, "w+")    # Add vcf file (excluding header) to intermediate file    for line in vcf:        if line.startswith('##'):            continue        else:            ivcf.write(line)t# Create an empty dict to store intermediate files initermediate_files_dict = {}     #   intermediate_files_dict[intermediate_file] = #vcf = open('BL_109.vcf')#print(vcf)# Create intermediate file#f = open("test.intermediate.file", "w+")#for line in vcf:#    if line.startswith('##'):#        continue#    else:#        continue                