### Things to add later:## Interpreter#!/bin/python## Read input or pass files list as input to script... # input = raw_input('vcf file: ')## Set wdimport osimport globimport pandas as pd## EDIT SO STORE IN OBJECT LIKE DICT OR SMTH INSTEAD# Read all vcf files in dirfile_list = glob.glob("*.vcf")for f in file_list:    # Get file name    filename = os.path.basename(f)    # Open file    vcf = open(filename)    # Create intermediate file    i_file = str.removesuffix(filename, ".vcf") + ".intermediate"    ivcf = open(i_file, "w+")    # Add vcf file (excluding header) to intermediate file    for line in vcf:        if line.startswith('##'):            continue        else:            ivcf.write(line)# Create an empty dict to store intermediate files indf_dict = {}# Read all intermediate files in diri_file_list = glob.glob("*.intermediate")for f in i_file_list:    # Get filename    i_filename = os.path.basename(f)    # Read as pandas dataframe    data = pd.read_csv(f, sep = "\t")    df = pd.DataFrame(data)    # Filter for PASS only and select INFO field:     df = df[df["FILTER"] == "PASS"]    df = df[["INFO"]]    # Store in dict    df_dict[i_filename] = df     #   intermediate_files_dict[intermediate_file] = #vcf = open('BL_109.vcf')#print(vcf)# Create intermediate file#f = open("test.intermediate.file", "w+")#for line in vcf:#    if line.startswith('##'):#        continue#    else:#        continue                